// D:\RoomieRadar\frontend\src\pages/RoommateMatch/CompareView.jsx
import React, { useState } from "react";
import Card from "../../components/Card";
import Slider from "../../components/Slider";
import Button from "../../components/Button";
import ChartWrapper from "../../components/ChartWrapper";

const CompareView = () => {
  const [roommateA, setRoommateA] = useState({ name: "Alex", cleanliness: 8, sleep: 6, noise: 5 });
  const [roommateB, setRoommateB] = useState({ name: "Jamie", cleanliness: 5, sleep: 7, noise: 6 });

  const radarData = {
    labels: ["Cleanliness", "Sleep", "Noise"],
    datasets: [
      { label: roommateA.name, data: [roommateA.cleanliness, roommateA.sleep, roommateA.noise], backgroundColor: "rgba(255,165,0,0.2)", borderColor: "orange", borderWidth: 2 },
      { label: roommateB.name, data: [roommateB.cleanliness, roommateB.sleep, roommateB.noise], backgroundColor: "rgba(34,197,94,0.2)", borderColor: "green", borderWidth: 2 }
    ]
  };

  return (
    <div className="container">
      <h1 className="heading-orange">Compare Roommates</h1>

      <Card title="Compatibility Overview">
        <ChartWrapper type="radar" data={radarData} />
      </Card>

      <div className="grid-cards">
        <Card title={roommateA.name} highlight>
          <Slider min={0} max={10} value={roommateA.cleanliness} label="Cleanliness" onChange={(val) => setRoommateA({ ...roommateA, cleanliness: val })} />
          <Slider min={0} max={10} value={roommateA.sleep} label="Sleep" onChange={(val) => setRoommateA({ ...roommateA, sleep: val })} />
          <Slider min={0} max={10} value={roommateA.noise} label="Noise" onChange={(val) => setRoommateA({ ...roommateA, noise: val })} />
        </Card>

        <Card title={roommateB.name}>
          <Slider min={0} max={10} value={roommateB.cleanliness} label="Cleanliness" onChange={(val) => setRoommateB({ ...roommateB, cleanliness: val })} />
          <Slider min={0} max={10} value={roommateB.sleep} label="Sleep" onChange={(val) => setRoommateB({ ...roommateB, sleep: val })} />
          <Slider min={0} max={10} value={roommateB.noise} label="Noise" onChange={(val) => setRoommateB({ ...roommateB, noise: val })} />
        </Card>
      </div>

      <Button text="Compare Compatibility" type="primary" onClick={() => alert("Compatibility recalculated!")} />
    </div>
  );
};

export default CompareView;
