// src/pages/RoommateMatch/DaySimulation.jsx
import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './DaySimulation.css'; // we'll create this file next

// Mock data - in real app you would fetch based on match ID
const mockSimulations = {
  1: {
    name: "Fariha Tasnim",
    avatar: "https://api.dicebear.com/9.x/avataaars/svg?seed=Fariha&backgroundColor=fed7aa",
    compatibility: 92,
    dayActivities: [
      { time: "7:00 AM", activity: "Wake up early ‚òÄÔ∏è", description: "Fariha is an early riser ‚Äî gentle alarm, makes tea", compatibility: "Good match ‚Äî you're both morning people-ish" },
      { time: "8:30 AM", activity: "Morning study / work üìö", description: "Quiet study session with lo-fi music", compatibility: "Very compatible ‚Äî similar focus hours" },
      { time: "12:30 PM", activity: "Lunch together üçõ", description: "She cooks simple Bengali food ‚Äî offers to share", compatibility: "Nice ‚Äî she likes sharing food" },
      { time: "3:00 PM", activity: "Short nap or chill üò¥", description: "Likes a quick power nap", compatibility: "Okay ‚Äî you might need to be quiet" },
      { time: "6:30 PM", activity: "Evening walk or hangout üö∂‚Äç‚ôÄÔ∏è", description: "Loves going out for cha or a short walk", compatibility: "Fun ‚Äî you both enjoy light socializing" },
      { time: "9:00 PM", activity: "Movie / series time üé¨", description: "Cuddly movie night with snacks", compatibility: "Perfect ‚Äî similar taste in chill evenings" },
      { time: "11:30 PM", activity: "Wind down & sleep üåô", description: "Lights out by midnight", compatibility: "Good ‚Äî close sleep schedules" },
    ]
  },
  // You can add more matches here with different schedules
};

// Fallback if no match found
const defaultSimulation = {
  name: "Unknown Roomie",
  avatar: "https://api.dicebear.com/9.x/avataaars/svg?seed=Unknown&backgroundColor=cccccc",
  compatibility: 0,
  dayActivities: [
    { time: "‚Äî", activity: "No data", description: "No simulation available for this match", compatibility: "" },
  ]
};

const DaySimulation = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const simulation = mockSimulations[id] || defaultSimulation;

  return (
    <div className="simulation-page">
      <div className="container">
        <button className="back-btn" onClick={() => navigate('/matches')}>
          ‚Üê Back to Matches
        </button>

        <h1 className="page-title">One Day with {simulation.name}</h1>
        <p className="subtitle">
          Compatibility: <strong>{simulation.compatibility}%</strong> ‚ô°
        </p>

        <div className="avatar-header">
          <img 
            src={simulation.avatar} 
            alt={`${simulation.name}'s avatar`} 
            className="simulation-avatar" 
          />
          <h2 className="roomie-name">{simulation.name}</h2>
        </div>

        <div className="timeline">
          {simulation.dayActivities.map((item, index) => (
            <div key={index} className="timeline-item">
              <div className="time-bubble">{item.time}</div>
              
              <div className="activity-card">
                <div className="activity-emoji">{item.activity}</div>
                <div className="activity-description">{item.description}</div>
                {item.compatibility && (
                  <div className="compat-note">
                    <span className="note-label">With you:</span> {item.compatibility}
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>

        <div className="summary-box">
          <h3>Quick Summary ‚ô°</h3>
          <p>
            {simulation.name} seems like a very cozy and balanced roommate!
            You both have quite similar habits in most areas ‚Äî especially morning & evening routines.
          </p>
          <p className="tip">
            Tip: Keep the kitchen clean together and plan some chai breaks ‚Äî you'll get along great! ‚òï‚ú®
          </p>
        </div>
      </div>
    </div>
  );
};

export default DaySimulation;
