// D:\RoomieRadar\frontend\src\pages\Dashboard/Dashboard.jsx
import React, { useState } from "react";
import Card from "../../components/Card";
import Button from "../../components/Button";
import Slider from "../../components/Slider";
import { Radar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  RadialLinearScale,
  PointElement,
  LineElement,
  Filler,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(RadialLinearScale, PointElement, LineElement, Filler, Tooltip, Legend);

const Dashboard = () => {
  const [cleanliness, setCleanliness] = useState(7);
  const [sleep, setSleep] = useState(6);
  const [noise, setNoise] = useState(5);

  const radarData = {
    labels: ["Cleanliness", "Sleep", "Noise"],
    datasets: [
      {
        label: "You",
        data: [cleanliness, sleep, noise],
        backgroundColor: "rgba(255,165,0,0.2)",
        borderColor: "rgba(255,165,0,1)",
        borderWidth: 2,
        pointBackgroundColor: "orange",
      },
      {
        label: "Top Match",
        data: [8, 7, 6],
        backgroundColor: "rgba(34,197,94,0.2)",
        borderColor: "rgba(34,197,94,1)",
        borderWidth: 2,
        pointBackgroundColor: "green",
      },
    ],
  };

  return (
    <div className="container">
      <h1 className="heading-orange">Your Compatibility Dashboard</h1>

      <Card title="Compatibility Overview">
        <Radar data={radarData} />
      </Card>

      <Card title="Adjust Preferences (What-If)">
        <Slider
          min={0}
          max={10}
          step={1}
          value={cleanliness}
          label="Cleanliness Preference"
          onChange={setCleanliness}
        />
        <Slider
          min={0}
          max={10}
          step={1}
          value={sleep}
          label="Sleep Schedule Preference"
          onChange={setSleep}
        />
        <Slider
          min={0}
          max={10}
          step={1}
          value={noise}
          label="Noise Tolerance"
          onChange={setNoise}
        />
        <Button text="Update Compatibility" type="primary" onClick={() => alert("Updated!")} />
      </Card>
    </div>
  );
};

export default Dashboard;
