// D:\RoomieRadar\frontend\src\pages\Dashboard\Dashboard.jsx
import React, { useState } from "react";
import Card from "../../components/Card";
import Button from "../../components/Button";
import Slider from "../../components/Slider";
import { Radar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  RadialLinearScale,
  PointElement,
  LineElement,
  Filler,
  Tooltip,
  Legend,
} from "chart.js";
import { useUser } from "../../context/UserContext";

ChartJS.register(RadialLinearScale, PointElement, LineElement, Filler, Tooltip, Legend);

const Dashboard = () => {
  const { answers, updateAnswers } = useUser();

  // Local state for What-If adjustments
  const [cleanliness, setCleanliness] = useState(answers.cleanliness);
  const [sleep, setSleep] = useState(answers.sleep);
  const [noise, setNoise] = useState(answers.noise);

  const handleUpdate = () => {
    updateAnswers({ cleanliness, sleep, noise });
    alert("Preferences updated!");
  };

  const radarData = {
    labels: ["Cleanliness", "Sleep", "Noise"],
    datasets: [
      {
        label: "You",
        data: [answers.cleanliness, answers.sleep, answers.noise],
        backgroundColor: "rgba(255,165,0,0.2)",
        borderColor: "rgba(255,165,0,1)",
        borderWidth: 2,
        pointBackgroundColor: "orange",
      },
      {
        label: "Top Match",
        data: [8, 7, 6], // Mock match data
        backgroundColor: "rgba(34,197,94,0.2)",
        borderColor: "rgba(34,197,94,1)",
        borderWidth: 2,
        pointBackgroundColor: "green",
      },
    ],
  };

  return (
    <div className="p-6 max-w-5xl mx-auto space-y-6">
      <h1 className="text-3xl font-bold text-orange-500 mb-6">Your Compatibility Dashboard</h1>

      {/* Radar Chart */}
      <Card title="Compatibility Overview">
        <Radar data={radarData} />
      </Card>

      {/* What-If Sliders */}
      <Card title="Adjust Preferences (What-If)">
        <Slider
          min={0}
          max={10}
          step={1}
          value={cleanliness}
          label="Cleanliness Preference"
          onChange={setCleanliness}
        />
        <Slider
          min={0}
          max={10}
          step={1}
          value={sleep}
          label="Sleep Schedule Preference"
          onChange={setSleep}
        />
        <Slider
          min={0}
          max={10}
          step={1}
          value={noise}
          label="Noise Tolerance"
          onChange={setNoise}
        />
        <Button text="Update Compatibility" type="primary" onClick={handleUpdate} />
      </Card>
    </div>
  );
};

export default Dashboard;
