// src/pages/CompareView/CompareView.jsx
import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './CompareView.css';

// Mock current user (you) ‚Äî with full answer text
const mockCurrentUser = {
  name: "Noshin",
  avatar: "https://api.dicebear.com/9.x/avataaars/svg?seed=Noshin&backgroundColor=b6e3f4",
  compatibility: 100,
  answers: {
    cleanliness: { score: 8, text: "Moderately clean is perfect" },
    sleep: { score: 6, text: "Pretty flexible!" },
    noise: { score: 7, text: "Some background noise is okay" },
    guests: { score: 4, text: "Rarely ‚Äî maybe once every 1‚Äì2 months" },
    sharing: { score: 7, text: "We can share sometimes, but let's talk first" },
    conflict: { score: 9, text: "I talk about it right away ‚Äî clear the air fast!" },
    pets: { score: 5, text: "I'm neutral ‚Äî I don't mind but also don't need them" },
  },
};

// Mock matches ‚Äî now with full answer text for each question
const mockMatches = {
  "1": {
    name: "Fariha Tasnim",
    avatar: "https://api.dicebear.com/9.x/avataaars/svg?seed=Fariha&backgroundColor=fed7aa",
    age: 21,
    hall: "Begum Rokeya Hall",
    whatsapp: "+8801712345678",
    answers: {
      cleanliness: { score: 9, text: "‚ú® I need everything super tidy!" },
      sleep: { score: 7, text: "Pretty flexible!" },
      noise: { score: 6, text: "Some background noise is okay" },
      guests: { score: 5, text: "A few times a month is perfect" },
      sharing: { score: 6, text: "We can share sometimes, but let's talk first" },
      conflict: { score: 8, text: "I wait a bit, then bring it up calmly" },
      pets: { score: 4, text: "I'm neutral ‚Äî I don't mind but also don't need them" },
    },
  },
  "2": {
    name: "Tahmid Hasan",
    avatar: "https://api.dicebear.com/9.x/avataaars/svg?seed=Tahmid&backgroundColor=ffdfbf",
    age: 22,
    hall: "Shahid Minar Hall",
    whatsapp: "+8801812349876",
    answers: {
      cleanliness: { score: 7, text: "Moderately clean is perfect" },
      sleep: { score: 5, text: "Night owl ~ midnight or later" },
      noise: { score: 8, text: "Noise doesn't really bother me" },
      guests: { score: 7, text: "Almost every week ‚Äî my place is the hangout spot!" },
      sharing: { score: 8, text: "What's mine is yours! Community fridge energy ‚ô°" },
      conflict: { score: 6, text: "I wait a bit, then bring it up calmly" },
      pets: { score: 6, text: "I'm okay with pets if they're well-behaved" },
    },
  },
  "3": {
    name: "Ayesha Siddiqua",
    avatar: "https://api.dicebear.com/9.x/avataaars/svg?seed=Ayesha&backgroundColor=c1f6c3",
    age: 20,
    hall: "Pritilata Hall",
    whatsapp: "+8801918765432",
    answers: {
      cleanliness: { score: 8, text: "Moderately clean is perfect" },
      sleep: { score: 8, text: "Early bird ~ 10pm to 6am" },
      noise: { score: 5, text: "Noise doesn't really bother me" },
      guests: { score: 3, text: "Rarely ‚Äî maybe once every 1‚Äì2 months" },
      sharing: { score: 5, text: "We can share sometimes, but let's talk first" },
      conflict: { score: 9, text: "I talk about it right away ‚Äî clear the air fast!" },
      pets: { score: 7, text: "I LOVE pets! The more the merrier ‚ô°" },
    },
  },
  "4": {
    name: "Rafiul Islam",
    avatar: "https://api.dicebear.com/9.x/avataaars/svg?seed=Rafi&backgroundColor=fed7aa",
    age: 23,
    hall: "Sher-e-Bangla Hall",
    whatsapp: "+8801719876543",
    answers: {
      cleanliness: { score: 6, text: "A little mess is cozy" },
      sleep: { score: 6, text: "Pretty flexible!" },
      noise: { score: 7, text: "Some background noise is okay" },
      guests: { score: 6, text: "A few times a month is perfect" },
      sharing: { score: 9, text: "What's mine is yours! Community fridge energy ‚ô°" },
      conflict: { score: 7, text: "I wait a bit, then bring it up calmly" },
      pets: { score: 8, text: "I LOVE pets! The more the merrier ‚ô°" },
    },
  },
};

const CompareView = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const match = mockMatches[id] || {
    name: "Unknown",
    age: "?",
    hall: "‚Äî",
    whatsapp: null,
    answers: {},
  };

  const [showContact, setShowContact] = useState(false);

  // Calculate compatibility score (higher = more compatible)
  const calculateCompatibility = () => {
    if (match.name === "Unknown") return 0;

    const categories = ['cleanliness', 'sleep', 'noise', 'guests', 'sharing', 'conflict', 'pets'];
    
    let totalDiff = 0;
    categories.forEach(key => {
      const yourValue = mockCurrentUser.answers[key]?.score || 5;
      const matchValue = match.answers[key]?.score || 5;
      totalDiff += Math.abs(yourValue - matchValue);
    });

    const avgDiff = totalDiff / categories.length;
    return Math.max(0, Math.round(100 - avgDiff * 9));
  };

  const compatibilityScore = calculateCompatibility();

  const categories = [
    { label: "Cleanliness", key: "cleanliness" },
    { label: "Sleep Schedule", key: "sleep" },
    { label: "Noise Tolerance", key: "noise" },
    { label: "Guests / Social Life", key: "guests" },
    { label: "Sharing Food & Items", key: "sharing" },
    { label: "Conflict Resolution", key: "conflict" },
    { label: "Pet Compatibility", key: "pets" },
  ];

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text)
      .then(() => alert('Copied to clipboard!'))
      .catch(err => console.error('Failed to copy:', err));
  };

  return (
    <div className="compare-page">
      <div className="container">
        <h1 className="page-title">Side-by-Side Comparison</h1>
        <p className="subtitle">You vs {match.name} ‚Äî let's see how you match~ ‚ô°</p>

        <div className="comparison-grid">
          {/* Your Column */}
          <div className="profile-column you-column">
            <div className="column-title">YOU</div>

            <div className="avatar-container">
              <img src={mockCurrentUser.avatar} alt="Your avatar" className="avatar" />
            </div>

            <div className="name">{mockCurrentUser.name}</div>

            <div className="score-big">{mockCurrentUser.compatibility}%</div>

            <div className="details-list">
              {categories.map((cat) => (
                <div key={cat.key} className="detail-item">
                  <span className="detail-label">{cat.label}</span>
                  <span className="detail-value">
                    {mockCurrentUser.answers[cat.key]?.score || "?"} / 10
                    <br />
                    <small className="answer-text">
                      {mockCurrentUser.answers[cat.key]?.text || "‚Äî"}
                    </small>
                  </span>
                </div>
              ))}
            </div>
          </div>

          {/* Match Column */}
          <div className="profile-column match-column">
            <div className="column-title">{match.name}</div>

            <div className="avatar-container">
              <img src={match.avatar} alt={`${match.name}'s avatar`} className="avatar" />
            </div>

            <div className="name">{match.name}</div>

            <div className="user-info-line">
              Age: <strong>{match.age}</strong> | Hall: <strong>{match.hall}</strong>
            </div>

            <div className={`score-big ${compatibilityScore >= 80 ? 'high' : compatibilityScore >= 65 ? 'medium' : 'low'}`}>
              {compatibilityScore}%
            </div>

            <div className="details-list">
              {categories.map((cat) => (
                <div key={cat.key} className="detail-item">
                  <span className="detail-label">{cat.label}</span>
                  <span className="detail-value">
                    {match.answers[cat.key]?.score || "?"} / 10
                    <br />
                    <small className="answer-text">
                      {match.answers[cat.key]?.text || "‚Äî"}
                    </small>
                  </span>
                </div>
              ))}
            </div>

            {/* Contact Button */}
            {match.whatsapp && (
              <div className="contact-section">
                <button
                  className="contact-btn"
                  onClick={() => setShowContact(!showContact)}
                >
                  Contact {match.name} ‚ô°
                </button>

                {showContact && (
                  <div className="contact-info-popup">
                    <div className="contact-row">
                      <span className="contact-label">WhatsApp:</span>
                      <a
                        href={`https://wa.me/${match.whatsapp.replace(/\D/g, '')}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="contact-value whatsapp"
                      >
                        {match.whatsapp}
                      </a>
                      <button
                        className="copy-btn"
                        onClick={() => copyToClipboard(match.whatsapp)}
                        title="Copy WhatsApp number"
                      >
                        üìã
                      </button>
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        <button className="back-btn" onClick={() => navigate('/matches')}>
          ‚Üê Back to Matches
        </button>
      </div>
    </div>
  );
};

export default CompareView;
