// src/components/ChartWrapper.jsx
import React from "react";
import PropTypes from "prop-types";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  RadialLinearScale,
  Filler,
  Tooltip,
  Legend,
} from "chart.js";
import { Radar, Bar, Line } from "react-chartjs-2";

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  RadialLinearScale,
  Filler,
  Tooltip,
  Legend
);

/**
 * ChartWrapper Component
 *
 * Props:
 * - type: 'radar' | 'bar' | 'line'
 * - data: chart data object
 * - options: chart options object (optional)
 * - className: string, additional CSS classes
 */
const ChartWrapper = ({ type = "radar", data, options = {}, className }) => {
  const chartProps = { data, options, className };

  switch (type) {
    case "bar":
      return <Bar {...chartProps} />;
    case "line":
      return <Line {...chartProps} />;
    case "radar":
    default:
      return <Radar {...chartProps} />;
  }
};

ChartWrapper.propTypes = {
  type: PropTypes.oneOf(["radar", "bar", "line"]),
  data: PropTypes.object.isRequired,
  options: PropTypes.object,
  className: PropTypes.string,
};

export default ChartWrapper;
