// src/pages/Dashboard/Dashboard.jsx
import React, { useState, useEffect } from 'react';
import ChartWrapper from '../../components/ChartWrapper'; // adjust path as needed
import './Dashboard.css';

const Dashboard = () => {
  const [answers, setAnswers] = useState({
    cleanliness: 5,
    sleep: 5,
    noise: 5,
    guests: 5,
    sharing: 5,
    conflict: 5,
    petsAttitude: 5,
    petsResponsibility: 5,
  });

  const [visibleQuestions, setVisibleQuestions] = useState(0);

  // Staggered reveal of questions
  useEffect(() => {
    if (visibleQuestions < questions.length) {
      const timer = setTimeout(() => {
        setVisibleQuestions(prev => prev + 1);
      }, 380);
      return () => clearTimeout(timer);
    }
  }, [visibleQuestions]);

  const questions = [
    {
      id: 'cleanliness',
      question: "How do you usually handle shared spaces?",
      options: [
        { text: "I clean everything immediately — sparkling clean life!", score: 9 },
        { text: "I tidy up daily, but I'm not obsessed", score: 7 },
        { text: "I clean when it starts looking messy", score: 4 },
        { text: "Cleaning? I'll do it... eventually... maybe", score: 2 },
      ],
    },
    {
      id: 'sleep',
      question: "What's your sleep rhythm like?",
      options: [
        { text: "Early to bed, early to rise ~ very consistent", score: 9 },
        { text: "Midnight is my golden hour", score: 6 },
        { text: "I'm a proud night owl (2–5 AM gang)", score: 3 },
        { text: "Sleep schedule? I just crash when I can't anymore", score: 1 },
      ],
    },
    {
      id: 'noise',
      question: "How do you feel about background sounds?",
      options: [
        { text: "I love music, calls, games — the livelier the better!", score: 9 },
        { text: "Some noise is fine, but not too loud or constant", score: 6 },
        { text: "I prefer quiet most of the day", score: 3 },
        { text: "I need almost complete silence to focus / relax", score: 1 },
      ],
    },
    {
      id: 'guests',
      question: "How often do you have friends over?",
      options: [
        { text: "Almost every week — my place is the hangout spot!", score: 9 },
        { text: "A few times a month is perfect", score: 6 },
        { text: "Rarely — maybe once every 1–2 months", score: 3 },
        { text: "Almost never — I like my space private", score: 1 },
      ],
    },
    {
      id: 'sharing',
      question: "Sharing food & stuff — your vibe?",
      options: [
        { text: "What's mine is yours! Community fridge energy ♡", score: 9 },
        { text: "We can share sometimes, but let's talk first", score: 6 },
        { text: "I prefer keeping my things separate", score: 3 },
        { text: "My food is my food. No sharing, please.", score: 1 },
      ],
    },
    {
      id: 'conflict',
      question: "When there's a disagreement, how do you handle it? ",
      options: [
        { text: "I talk about it right away — clear the air fast!", score: 9 },
        { text: "I wait a bit, then bring it up calmly", score: 6 },
        { text: "I avoid conflict if possible... maybe drop hints", score: 3 },
        { text: "I just keep it inside until I explode or leave", score: 1 },
      ],
    },
    {
      id: 'petsAttitude',
      question: "How do you feel about living with pets?",
      options: [
        { text: "I LOVE pets! The more the merrier ♡", score: 9 },
        { text: "I'm okay with pets if they're well-behaved", score: 6 },
        { text: "I'm neutral — I don't mind but also don't need them", score: 4 },
        { text: "I'd rather live without pets (allergies / preference)", score: 2 },
      ],
    },
    {
      id: 'petsResponsibility',
      question: "If there's a pet in the house, how do you feel about helping out?",
      options: [
        { text: "I'm happy to walk/feed/clean after them — team player!", score: 9 },
        { text: "I can help sometimes, but prefer the owner does most", score: 6 },
        { text: "I'll do the bare minimum if I have to", score: 3 },
        { text: "Pet care is 100% the owner's responsibility — not mine", score: 1 },
      ],
    },
  ];

  const handleAnswer = (id, score) => {
    setAnswers(prev => ({ ...prev, [id]: score }));
  };

  // Mock best match (replace with real data later)
  const bestMatch = {
    cleanliness: 8,
    sleep: 7,
    noise: 6,
    guests: 5,
    sharing: 7,
    conflict: 8,
    petsAttitude: 7,
    petsResponsibility: 7,
  };

  const radarData = {
    labels: [
      'Cleanliness',
      'Sleep Rhythm',
      'Noise Tolerance',
      'Guests',
      'Sharing',
      'Conflict Style',
      'Pet Friendliness',
      'Pet Care Willingness',
    ],
    datasets: [
      {
        label: 'Your Vibe',
        data: Object.values(answers),
        backgroundColor: 'rgba(255, 123, 140, 0.22)',
        borderColor: 'rgba(255, 123, 140, 0.9)',
        borderWidth: 3,
        pointBackgroundColor: '#fff',
        pointBorderColor: '#ff7b8c',
        pointHoverBackgroundColor: '#ff7b8c',
        pointHoverBorderColor: '#fff',
      },
      {
        label: 'Dream Roomie',
        data: Object.values(bestMatch),
        backgroundColor: 'rgba(167, 230, 215, 0.24)',
        borderColor: 'rgba(167, 230, 215, 0.9)',
        borderWidth: 3,
        pointBackgroundColor: '#fff',
        pointBorderColor: '#a7e6d7',
        pointHoverBackgroundColor: '#a7e6d7',
        pointHoverBorderColor: '#fff',
      },
    ],
  };

  const radarOptions = {
    scales: {
      r: {
        angleLines: { color: 'rgba(166,111,92,0.25)' },
        grid: { color: 'rgba(166,111,92,0.18)' },
        pointLabels: {
          font: { size: 14, weight: 600, family: 'Nunito' },
          color: '#a66f5c',
        },
        ticks: {
          stepSize: 2,
          color: '#8a7580',
          backdropColor: 'transparent',
        },
        min: 0,
        max: 10,
      },
    },
  };

  return (
    <div className="dashboard-page">
      <div className="container">
        <h1 className="page-title">RoomieRadar Dashboard</h1>
        <p className="subtitle">
          Discover your roommate personality & cozy pet compatibility
        </p>

        {/* Radar Chart */}
        <div className="card radar-card">
          <h2 className="card-title">Compatibility Radar ✧</h2>
          <div className="radar-container">
            <ChartWrapper
              type="radar"
              data={radarData}
              options={radarOptions}
              height="520px"
            />
          </div>
        </div>

        {/* Questions */}
        <div className="card">
          <h2 className="card-title">Let's get to know your roomie soul~ ✿</h2>
          <p className="subtitle" style={{ marginBottom: '2rem', fontSize: '1.22rem' }}>
            Choose what feels most like you — the radar updates in real time!
          </p>

          <div className="questions-grid">
            {questions.map((q, index) => (
              <div
                key={q.id}
                className={`question-item ${index < visibleQuestions ? 'visible' : ''}`}
                style={{ transitionDelay: `${index * 160}ms` }}
              >
                <h3 className="question-text">{q.question}</h3>

                <div className="options">
                  {q.options.map((opt, idx) => {
                    const isSelected = answers[q.id] === opt.score;
                    return (
                      <button
                        key={idx}
                        className={`option ${isSelected ? 'selected' : ''}`}
                        onClick={() => handleAnswer(q.id, opt.score)}
                      >
                        {opt.text}
                      </button>
                    );
                  })}
                </div>

                <div className="score-display">
                  Your vibe: <strong>{answers[q.id]}</strong> / 10
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Decorative elements */}
      <div className="decor-bear-1" />
      <div className="decor-bear-2" />
    </div>
  );
};

export default Dashboard;
