// src/components/Sidebar.jsx
import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "./Sidebar.css"; // ← make sure you create/update this file

const Sidebar = () => {
  const [isCollapsed, setIsCollapsed] = useState(false);

  const links = [
    { name: "Dashboard", path: "/dashboard" },
    { name: "Matches", path: "/matches" },
    { name: "Discovery", path: "/discovery" }, // ← optional: added if you have it
    { name: "Profile", path: "/profile" },
  ];

  const toggleSidebar = () => {
    setIsCollapsed(!isCollapsed);
  };

  return (
    <aside className={`sidebar ${isCollapsed ? "collapsed" : ""}`}>
      {/* Header with toggle button */}
      <div className="sidebar-header">
        {!isCollapsed && <h2 className="sidebar-title">RoomieRadar</h2>}
        
        <button 
          className="toggle-btn" 
          onClick={toggleSidebar}
          aria-label={isCollapsed ? "Expand sidebar" : "Collapse sidebar"}
        >
          {isCollapsed ? "→" : "←"}
        </button>
      </div>

      {/* Navigation Links */}
      <nav className="sidebar-links">
        {links.map((link) => (
          <NavLink
            key={link.name}
            to={link.path}
            className={({ isActive }) =>
              isActive 
                ? "sidebar-link active-sidebar-link" 
                : "sidebar-link"
            }
            title={isCollapsed ? link.name : undefined} // tooltip when collapsed
          >
            {/* Optional: You can add icons here later */}
            {!isCollapsed && link.name}
          </NavLink>
        ))}
      </nav>
    </aside>
  );
};

export default Sidebar;
