// src/pages/CompareView/CompareView.jsx
import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './CompareView.css';

// Mock current user (you)
const mockCurrentUser = {
  name: "Noshin Syara",
  avatar: "https://api.dicebear.com/9.x/avataaars/svg?seed=Noshin&backgroundColor=b6e3f4",
  compatibility: 100,
  cleanliness: 8,
  sleep: 6,
  noise: 7,
  guests: 4,
  sharing: 7,
  conflict: 9,
  pets: 5,
  temperature: 6,
  cooking: 7,
  studyTime: 8,
};

// Mock database of potential roommates (Bengali names)
const mockMatches = {
  1: {
    name: "Ayesha Akter",
    avatar: "https://api.dicebear.com/9.x/avataaars/svg?seed=Ayesha&backgroundColor=ffdfbf",
    cleanliness: 9,
    sleep: 7,
    noise: 6,
    guests: 5,
    sharing: 6,
    conflict: 8,
    pets: 7,
    temperature: 7,
    cooking: 6,
    studyTime: 7,
    contact: {
      email: "ayesha.akter@example.com",
      whatsapp: "+8801712345678",
    },
  },
  2: {
    name: "Rifat Hasan",
    avatar: "https://api.dicebear.com/9.x/avataaars/svg?seed=Rifat&backgroundColor=c1f6c3",
    cleanliness: 6,
    sleep: 5,
    noise: 8,
    guests: 8,
    sharing: 9,
    conflict: 6,
    pets: 4,
    temperature: 5,
    cooking: 8,
    studyTime: 9,
    contact: {
      email: "rifat.hasan@example.com",
      whatsapp: "+8801918765432",
    },
  },
  3: {
    name: "Sumaiya Rahman",
    avatar: "https://api.dicebear.com/9.x/avataaars/svg?seed=Sumaiya&backgroundColor=fed7aa",
    cleanliness: 7,
    sleep: 8,
    noise: 5,
    guests: 3,
    sharing: 6,
    conflict: 8,
    pets: 9,
    temperature: 8,
    cooking: 5,
    studyTime: 6,
    contact: {
      email: "sumaiya.rahman@example.com",
      whatsapp: "+8801811122334",
    },
  },
};

const CompareView = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  // Get match from mock data
  const match = mockMatches[id] || {
    name: "Unknown",
    compatibility: 0,
    cleanliness: "?",
    sleep: "?",
    noise: "?",
    guests: "?",
    sharing: "?",
    conflict: "?",
    pets: "?",
    temperature: "?",
    cooking: "?",
    studyTime: "?",
    contact: {},
  };

  // Calculate compatibility score
  const calculateCompatibility = () => {
    if (match.name === "Unknown") return 0;

    const categories = [
      'cleanliness', 'sleep', 'noise', 'guests', 'sharing',
      'conflict', 'pets', 'temperature', 'cooking', 'studyTime'
    ];

    let totalDiff = 0;
    categories.forEach(key => {
      const yourValue = mockCurrentUser[key] || 5;
      const matchValue = match[key] !== undefined ? match[key] : 5;
      totalDiff += Math.abs(yourValue - matchValue);
    });

    const avgDiff = totalDiff / categories.length;
    return Math.max(0, Math.round(100 - avgDiff * 7));
  };

  const displayedCompatibility = calculateCompatibility();

  const [showContact, setShowContact] = useState(false);

  const categories = [
    { label: "Cleanliness", key: "cleanliness" },
    { label: "Sleep Schedule", key: "sleep" },
    { label: "Noise Tolerance", key: "noise" },
    { label: "Guests / Social Life", key: "guests" },
    { label: "Sharing Food & Items", key: "sharing" },
    { label: "Conflict Resolution", key: "conflict" },
    { label: "Pet Compatibility", key: "pets" },
    { label: "Temperature Preference", key: "temperature" },
    { label: "Cooking & Smells", key: "cooking" },
    { label: "Study / Work Hours", key: "studyTime" },
  ];

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text)
      .then(() => alert('Copied to clipboard!'))
      .catch(err => console.error('Failed to copy:', err));
  };

  return (
    <div className="compare-page">
      <div className="container">
        <h1 className="page-title">Side-by-Side Comparison</h1>
        <p className="subtitle">You vs {match.name} ‚Äî let's see how you match~ ‚ô°</p>

        <div className="comparison-grid">
          {/* Your Column */}
          <div className="profile-column you-column">
            <div className="column-title">YOU</div>

            <div className="avatar-container">
              <img src={mockCurrentUser.avatar} alt="Your avatar" className="avatar" />
            </div>

            <div className="name">{mockCurrentUser.name}</div>
            <div className="score-big">{mockCurrentUser.compatibility}%</div>

            <div className="details-list">
              {categories.map((cat) => (
                <div key={cat.key} className="detail-item">
                  <span className="detail-label">{cat.label}</span>
                  <span className="detail-value">{mockCurrentUser[cat.key]}/10</span>
                </div>
              ))}
            </div>
          </div>

          {/* Match Column */}
          <div className="profile-column match-column">
            <div className="column-title">{match.name}</div>

            <div className="avatar-container">
              <img src={match.avatar} alt={`${match.name}'s avatar`} className="avatar" />
            </div>

            <div className="name">{match.name}</div>
            <div className="score-big">{displayedCompatibility}%</div>

            <div className="details-list">
              {categories.map((cat) => (
                <div key={cat.key} className="detail-item">
                  <span className="detail-label">{cat.label}</span>
                  <span className="detail-value">
                    {match[cat.key] !== undefined ? match[cat.key] : "?"}/10
                  </span>
                </div>
              ))}
            </div>

            {/* Contact Button */}
            {match.contact?.email || match.contact?.whatsapp ? (
              <div className="contact-section">
                <button
                  className="contact-btn"
                  onClick={() => setShowContact(!showContact)}
                >
                  Contact {match.name} ‚ô°
                </button>

                {showContact && (
                  <div className="contact-info-popup">
                    {match.contact.email && (
                      <div className="contact-row">
                        <span className="contact-label">Email:</span>
                        <a href={`mailto:${match.contact.email}`} className="contact-value">
                          {match.contact.email}
                        </a>
                        <button
                          className="copy-btn"
                          onClick={() => copyToClipboard(match.contact.email)}
                          title="Copy email"
                        >
                          üìã
                        </button>
                      </div>
                    )}

                    {match.contact.whatsapp && (
                      <div className="contact-row">
                        <span className="contact-label">WhatsApp:</span>
                        <a
                          href={`https://wa.me/${match.contact.whatsapp.replace(/\D/g, '')}`}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="contact-value whatsapp"
                        >
                          {match.contact.whatsapp}
                        </a>
                        <button
                          className="copy-btn"
                          onClick={() => copyToClipboard(match.contact.whatsapp)}
                          title="Copy WhatsApp number"
                        >
                          üìã
                        </button>
                      </div>
                    )}
                  </div>
                )}
              </div>
            ) : null}
          </div>
        </div>

        <button className="back-btn" onClick={() => navigate('/matches')}>
          ‚Üê Back to Matches
        </button>
      </div>
    </div>
  );
};

export default CompareView;
