// D:\RoomieRadar\frontend\src\pages\Questionnaire\Questionnaire.jsx
import React, { useState } from "react";
import Button from "../../components/Button";
import { useUser } from "../../context/UserContext";

// Mock questions with options and numeric values
const questions = [
  {
    id: 1,
    question: "How clean do you like your room?",
    field: "cleanliness",
    options: [
      { text: "Very clean", value: 10 },
      { text: "Moderately clean", value: 7 },
      { text: "Messy is fine", value: 4 },
      { text: "Don’t care", value: 2 },
    ],
  },
  {
    id: 2,
    question: "What is your preferred sleep schedule?",
    field: "sleep",
    options: [
      { text: "Early bird (6am-10pm)", value: 10 },
      { text: "Flexible", value: 7 },
      { text: "Night owl (midnight+)", value: 4 },
      { text: "Doesn't matter", value: 2 },
    ],
  },
  {
    id: 3,
    question: "How much noise can you tolerate?",
    field: "noise",
    options: [
      { text: "Very little", value: 10 },
      { text: "Some noise is okay", value: 7 },
      { text: "Don't mind noise", value: 4 },
      { text: "Love a noisy environment", value: 2 },
    ],
  },
];

const Questionnaire = () => {
  const [current, setCurrent] = useState(0);
  const [showResults, setShowResults] = useState(false);
  const { answers, updateAnswers } = useUser();

  // Handle option selection for current question
  const handleOptionSelect = (value) => {
    const field = questions[current].field;
    updateAnswers({ [field]: value });

    if (current < questions.length - 1) {
      setCurrent(current + 1);
    } else {
      setShowResults(true);
    }
  };

  // Calculate a mock compatibility score
  const calculateScore = () => {
    const total = Object.values(answers).reduce((a, b) => a + b, 0);
    const max = questions.length * 10;
    return Math.round((total / max) * 100);
  };

  // Show results page
  if (showResults) {
    const score = calculateScore();
    return (
      <div className="p-6 max-w-xl mx-auto text-center">
        <h1 className="text-3xl font-bold mb-4">Your Compatibility Result</h1>
        <h2 className="text-4xl font-bold text-orange-500 mb-6">{score}%</h2>

        <Button
          text="Restart Questionnaire"
          type="primary"
          onClick={() => {
            updateAnswers({ cleanliness: 5, sleep: 5, noise: 5 });
            setCurrent(0);
            setShowResults(false);
          }}
        />
      </div>
    );
  }

  // Current question
  const question = questions[current];

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h1 className="text-3xl font-bold mb-6 text-center">Roommate Compatibility Questionnaire</h1>
      <h2 className="text-xl font-medium mb-4">{question.question}</h2>

      <div className="flex flex-col gap-3">
        {question.options.map((opt, idx) => (
          <Button
            key={idx}
            text={opt.text}
            type="primary"
            onClick={() => handleOptionSelect(opt.value)}
          />
        ))}
      </div>
    </div>
  );
};

export default Questionnaire;
