// src/pages/RoommateMatch/RoommateMatch.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './RoommateMatch.css';

const mockMatches = [
  { id: 1, name: "Fariha Tasnim", compatibility: 92, top: true },
  { id: 2, name: "Tahmid Hasan", compatibility: 88, top: false },
  { id: 3, name: "Ayesha Siddiqua", compatibility: 85, top: false },
  { id: 4, name: "Rafiul Islam", compatibility: 80, top: false },
  { id: 5, name: "Sumaiya Akter", compatibility: 78, top: false },
  { id: 6, name: "Zarin Tasnim", compatibility: 76, top: false },
  { id: 7, name: "Shakib Ahmed", compatibility: 74, top: false },
];

const RoommateMatch = () => {
  const navigate = useNavigate();
  const [roommates] = useState(mockMatches);

  const handleViewSimulation = (name) => {
    alert(`Opening one-day simulation for ${name}... ðŸ•’âœ¨`);
    // In real app â†’ navigate to /simulation/:id or open modal
  };

  const handleCompare = (id) => {
    navigate(`/compare/${id}`);
    // Or open modal/compare page with two profiles
  };

  return (
    <div className="matches-page">
      <div className="container">
        <h1 className="page-title">Your Roomie Matches</h1>
        <p className="subtitle">Here are the cutest matches for you~ â™¡</p>

        <div className="matches-grid">
          {roommates.map((rm) => (
            <div
              key={rm.id}
              className={`match-card ${rm.top ? 'top-match' : ''}`}
            >
              {rm.top && <div className="top-badge">Top Match! â˜…</div>}

              <div className="card-name">{rm.name}</div>

              <div className="compatibility-score">{rm.compatibility}%</div>
              <div className="score-label">Compatibility Score</div>

              <div className="button-group">
                <button
                  className="action-btn view-simulation-btn"
                  onClick={() => handleViewSimulation(rm.name)}
                >
                  View Day Simulation
                </button>

                <button
                  className="action-btn compare-btn"
                  onClick={() => handleCompare(rm.id)}
                >
                  Compare âœ§
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RoommateMatch;
